/// <reference types="node" />
import { PublicKey } from "@solana/web3.js";
type SerializableAccount = {
    pubkey: PublicKey;
    isSigner: boolean;
    isWritable: boolean;
};
type SerializableInstruction = {
    programId: PublicKey;
    accounts: SerializableAccount[];
    data: Buffer;
};
export { SerializableInstruction, SerializableAccount };
